import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

export default function App() {
  const targetDate = new Date("2026-02-09T00:00:00");
  const [timeLeft, setTimeLeft] = useState({});
  const [theme, setTheme] = useState("rose");
  const audioRef = useRef(null);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const diff = targetDate - now;
      setTimeLeft({
        days: Math.floor(diff / (1000*60*60*24)),
        hours: Math.floor((diff/(1000*60*60)) % 24),
        minutes: Math.floor((diff/(1000*60)) % 60),
        seconds: Math.floor((diff/1000) % 60),
      });
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const playMusic = () => audioRef.current.play();
  const pauseMusic = () => audioRef.current.pause();

  const themeClasses = {
    rose: "from-rose-50 to-red-100",
    gold: "from-yellow-50 to-yellow-200",
    lavender: "from-purple-50 to-purple-200"
  };

  return (
    <div className={
      "min-h-screen text-gray-800 bg-gradient-to-b " + themeClasses[theme]
    }>
      <audio ref={audioRef} src="/music.mp3" loop />

      <div className="p-6">
        <h1 className="text-4xl font-bold text-center mb-4">
          Piyush ❤️ Dhawal
        </h1>

        <motion.div 
          initial={{ opacity: 0, scale: 0.8 }} 
          animate={{ opacity: 1, scale: 1 }}
          className="text-center text-3xl my-4"
        >
          {timeLeft.days}d : {timeLeft.hours}h : {timeLeft.minutes}m : {timeLeft.seconds}s
        </motion.div>

        <div className="flex justify-center gap-4 my-6">
          <button onClick={() => setTheme("rose")}>Rose</button>
          <button onClick={() => setTheme("gold")}>Gold</button>
          <button onClick={() => setTheme("lavender")}>Lavender</button>
        </div>

        <div className="flex justify-center gap-4 mt-6">
          <button onClick={playMusic}>Play Music</button>
          <button onClick={pauseMusic}>Pause Music</button>
        </div>
      </div>
    </div>
  );
}
